import React from 'react'

const SinglePerson = ({ searchName, persons }) => {

    const searchResult = persons.filter(person => person.name.toLowerCase().startsWith(searchName.toLowerCase()))
    if (searchName === '') {
        return (
            <div>

            </div>
        )
    }
    else if (searchResult) {
        return (
            <div>
                <h2>Search Result</h2>
                <ul>
                    {searchResult.map(result=>
                        <p>{result.name} {person.number}</p>
                        )}
                </ul>
            </div>
        )
    } else {
        return (
            <div>
                <h2>Search Result</h2>
                <p>No result is found.</p>
            </div>
        )
    }

}

export default SinglePerson
