import React from 'react'

const SinglePerson = ({ searchName, person }) => {

    if(person.name.toLowerCase().startsWith(searchName.toLowerCase())){
        return (
            <div>
                <h2>Search Result</h2>
        <p>{person.name} {person.number}</p>
            </div>
        )
    }
    return (
        <div>
            <h2>Search Result</h2>
            <p>{searchName}</p>
        </div>
    )
}

export default SinglePerson
