import React from 'react'

const SinglePerson = ({ searchName, person }) => {

    const searchResult = person.filter(sPerson =>
        person.name.toLowerCase().startsWith(searchName.toLowerCase())
    )
    if (searchName === '') {
        return (
            <div>

            </div>
        )
    }
    else if (searchResult.length > 0) {
        return (
            <div>
                <h2>Search Result</h2>
                {searchResult.map(result =>
                    <p>{result.name} {result.number}</p>
                )}
            </div>
        )
    } else {
        return (
            <div>
                <h2>Search Result</h2>
                <p>No result is found.</p>
            </div>
        )
    }

}

export default SinglePerson
