                alert(
                    `the note '${note.content}' was already deleted from server`
                )
                setNotes(notes.filter(n => n.id !== id))
            })
    }

    return (
        <div>
            <h2>Phonebook</h2>
            <Filter searchName={searchName} setSearchName={setSearchName} />
            <SinglePerson searchName={searchName} persons={persons} />
            <h2>Add a new</h2>
            <PersonForm newName={newName} newNumber={newNumber} addName={addName}
                handleNewName={handleNewName} handleNewNumber={handleNewNumber}
            />
            <h2>Numbers</h2>
            {persons.map(person => <Persons key={person.name} person={person} />)}
        </div>
    )
}

export default App
